mapper.select:
select max(cyrybh) from pub_qyryxx
where
pub_qyryxx.HYLBDM=#{hylbdm,jdbcType=VARCHAR}
AND
pub_qyryxx.XZDXZQHDM =#{xzdxzqhdm,jdbcType=VARCHAR}
AND
pub_qyryxx.CYRYZTDM=#{cyryztdm,jdbcType=VARCHAR}
AND
 DATE_SUB(CURDATE(), INTERVAL 1 DAY ) <= date(pub_qyryxx.lrsj)

select
from pub_qyryxx left join pub_qyjbxx on pub_qyryxx.QYID=pub_qyjbxx.QYID
pub_qyjbxx.QYMC like CONCAT(CONCAT('%',#{qymc,jdbcType=VARCHAR}), '%')
AND pub_qyjbxx.QYBM like CONCAT(CONCAT('%',#{qybm,jdbcType=VARCHAR}), '%')
AND pub_qyjbxx.GXDWMC like CONCAT(CONCAT('%',#{gxdwmc,jdbcType=VARCHAR}), '%')
AND pub_qyryxx.XM like CONCAT(CONCAT('%',#{xm,jdbcType=VARCHAR}), '%')
AND pub_qyryxx.XB =#{xb,jdbcType=VARCHAR}
AND pub_qyryxx.XBDM =#{xbdm,jdbcType=VARCHAR}
AND pub_qyryxx.ZJHM like CONCAT(CONCAT('%',#{zjhm,jdbcType=VARCHAR}), '%')
AND pub_qyryxx.CYRYBH like CONCAT(CONCAT('%',#{cyrybh,jdbcType=VARCHAR}), '%')
AND pub_qyryxx.CYRYZTDM =#{cyryztdm,jdbcType=VARCHAR}
AND pub_qyryxx.SCBZ != 1 AND pub_qyryxx.QYID = #{qyid,jdbcType=INTEGER}
order by pub_qyryxx.RYID DESC
